            // 判断是否有重复用户名
            if (findName != 200) {
              // 验证邮箱格式
              if (emailValidation.test(data.email)) {
                _this.$axios({
                  url: '/api/v1/users',
                  method: 'post',
                  data: data
                }).then(function (res) {
                  if (res.data.code == 200) {
                    _this.getUsers();
                  } else {
                    _this.$router.push({
                      path: "/nopermisson"
                    });
                  }
                });
              } else {
                _this.dialogFormVisible = true;
                alert('Please Input Correct Email Format');
              }
            } else {
              _this.dialogFormVisible = true;
              alert('Repeat Username, Please Input Again');
            }
          } else {
            _this.dialogFormVisible = true;
            alert('please Input All Data');
          }
        } else {
          _this.$router.push({
            path: "/nopermisson"
          });
        }
      });
    },
