@import "../../styles/style.scss";
.el-carousel__item h3 {
  color: #475669;
  font-size: 14px;
  opacity: 0.75;
  line-height: 200px;
  margin: 0;
}

.el-carousel__item:nth-child(2n) {
  background-color: #99a9bf;
}

.el-carousel__item:nth-child(2n+1) {
  background-color: #d3dce6;
}
.container{
  .row{
    width: $width_screen;
    margin: $size0 auto;
    .banner{
      width: $width100;
    }
    .main{
      text-align: center;
      ul,li{margin: 0;padding: 0;text-decoration: none; list-style: none;}
      .tab{
        display: inline-block;
        padding: $size10;
        letter-spacing: $size3;
        &::after{
          content: '';
          display: inline-block;
          clear: both;
        }
        li{
          float: $position_left;
          a{
            padding: $size10 $size20;
            font-size: $font_size2;
            &:hover{ color:$color_a_hover;}
          }
        }
      }
      .main-recommend{
        text-align: left;
        width: $width_screen;
        margin: $size0 auto;
        overflow: hidden;
        .menu-item{
          padding: $size5;
          float: left;
          a{
            text-decoration: none;
            &:hover{transform: scale(1.05); transition-duration:0.5s;}
            .item-img{
              img{width: $width100;}
            }
            .item-info{
              span{display: block;padding: $size10 $size15;}
              width: $width100;
              background-color: $color_white;
              .info-title{
                font-size: $font_size2;
                color: $color_title;
                font-weight: $font_bold;
              }
              .info-user{
                font-size: $font_size1;
                color: $color_user_title;
                overflow: hidden;
                width: 100%;
              }
            }
          }
        }
      }
    }
  }
}
